import java.nio.charset.StandardCharsets;
import java.nio.file.Files;
import java.nio.file.Paths;

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectPath = groovyUtils.projectPath
def filePath = projectPath + "/data/EveryLangCorrectTextWithAllLang.txt"

def lines = Files.readAllLines(Paths.get(filePath), StandardCharsets.UTF_8);
def counter = testRunner.testCase.getPropertyValue("counter").toInteger()

if(lines.size() == 0) {
	testRunner.cancel("No test data in file")
} else if(counter + 1 >= lines.size()) {
	testRunner.testCase.setPropertyValue("isEnd", "true")
}

def data = lines[counter].split(";")

log.info("Lang: " + data[0] + "   " + "Text: " + data[1])
testRunner.testCase.setPropertyValue("lang", data[0])
testRunner.testCase.setPropertyValue("text", data[1])

if(!testRunner.testCase.getPropertyValue("isEnd").toBoolean()) { // not end
	def counter = testRunner.testCase.getPropertyValue("counter").toInteger()
	counter = counter + 1
	testRunner.testCase.setPropertyValue("counter", counter.toString())
	testRunner.gotoStepByName("SetupData")
}

def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectPath = groovyUtils.projectPath
def filePath = projectPath + "/data/PositiveAllLangAndOptions.txt"

FileInputStream fileStream = new FileInputStream(filePath);
DataInputStream dataStream = new DataInputStream(fileStream);
BufferedReader reader = new BufferedReader(new InputStreamReader(dataStream));

reader.lines()
	.map {line -> line.split(";")}
	.forEach { data ->
		log.info("Lang: " + data[0] + "   " + "Options: " + data[1])
		testRunner.testCase.setPropertyValue("lang", data[0])
		testRunner.testCase.setPropertyValue("options", data[1])
		testRunner.runTestStepByName("EmptyRequest")
	}

testRunner.gotoStepByName("TearDown")
