def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context)
def projectPath = groovyUtils.projectPath
def filePath = projectPath + "/" + context.expand('${#Project#dataDir}') + "/" + context.expand('${#TestCase#file}')

FileInputStream fileStream = new FileInputStream(filePath);
DataInputStream dataStream = new DataInputStream(fileStream);
BufferedReader reader = new BufferedReader(new InputStreamReader(dataStream));

reader.lines()
	.map {line -> line.split(";")}
	.forEach { data ->
		log.info("Lang: " + data[0] + "   " + "Options: " + data[1])
		testRunner.testCase.setPropertyValue("lang", data[0])
		testRunner.testCase.setPropertyValue("options", data[1])
		testRunner.runTestStepByName("checkEmptyRequest")
	}

testRunner.gotoStepByName("tearDown")
